import json

def main(playwright_response: str) -> dict:
    try:
        data = json.loads(playwright_response)

        if not data.get('success'):
            return {
                'error': 'Playwright analysis failed',
                'details': data.get('error', 'Unknown error')
            }

        analysis = data.get('analysis', {})
        categories = analysis.get('categories', {})

        # Extract critical and major issues
        critical_issues = []
        major_issues = []

        for category_name, items in categories.items():
            for item in items:
                if item.get('status') == 'fail':
                    issue_data = {
                        'category': category_name,
                        'name': item.get('name'),
                        'impact': item.get('impact'),
                        'details': item.get('details'),
                        'recommendations': item.get('recommendations', [])
                    }

                    if item.get('impact') == 'critical':
                        critical_issues.append(issue_data)
                    elif item.get('impact') == 'major':
                        major_issues.append(issue_data)

        # Prepare summary for LLM
        summary = {
            'url': data.get('url'),
            'overall_score': analysis.get('summary', {}).get('score', 0),
            'total_issues': len(critical_issues) + len(major_issues),
            'critical_count': len(critical_issues),
            'major_count': len(major_issues),
            'critical_issues': critical_issues[:5],  # Limit for token efficiency
            'major_issues': major_issues[:10],
            'performance': data.get('performance', {}),
            'report_url': data.get('reportUrl')
        }

        # Token optimization - create concise text for LLM
        llm_input = f"""Website: {summary['url']}
Score: {summary['overall_score']}%
Critical Issues ({summary['critical_count']}):
"""

        for issue in summary['critical_issues']:
            llm_input += f"- {issue['category']}: {issue['name']}\n"

        llm_input += f"\nMajor Issues ({summary['major_count']}):\n"
        for issue in summary['major_issues']:
            llm_input += f"- {issue['category']}: {issue['name']}\n"

        return {
            'summary': summary,
            'llm_input': llm_input,
            'needs_deep_analysis': summary['critical_count'] > 0 or summary['overall_score'] < 70
        }

    except Exception as e:
        return {
            'error': f'Processing failed: {str(e)}',
            'llm_input': 'Analysis data processing failed'
        }
